INSERT INTO `wp_posts` VALUES (11,1,'2022-06-05 05:38:57','2022-06-05 05:38:57','{\n    \"foce::story\": {\n        \"value\": \"Loin, tr\\u00e8s loin, au del\\u00e0 des monts Karma, \\u00e0 mille lieues des pays Mizukage et Kagura, demeurent les Nekos Nekos. Ils vivent en retrait, sur les c\\u00f4tes du Funato, un vaste oc\\u00e9an d\\u2019arbres fruitier. Un petit ruisseau coule en leur lieu et les approvisionne en r\\u00e8glalades n\\u00e9cessaires en tout genre; un pays paradisiaque... Sauf pour Kawaneko, le premier chat \\u00e0 avoir d\\u00e9velopp\\u00e9 une intol\\u00e9rance \\u00e0 la fleur d\\u2019oranger, qui malheureusement pour lui est la base de toute productions du pays. Serait-ce une maladie, un virus, le d\\u00e9but d\\u2019une pand\\u00e9mie ou un sort jet\\u00e9 par le sorcier Jaakuna ? Kawaneko et sa bande partent en qu\\u00eate de r\\u00e9ponses pour arr\\u00eater la malveillant Jaakuna et retrouver l\\u2019ordre chez les Nekos Nekos.\\n\\n\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2022-06-05 05:38:57\"\n    }\n}','','','trash','closed','closed','','0f463d93-1c14-40e6-8f4b-3cab28d25547','','','2022-06-05 05:38:57','2022-06-05 05:38:57','',0,'http://koukaki.local/0f463d93-1c14-40e6-8f4b-3cab28d25547/',0,'customize_changeset','',0);
