INSERT INTO `wp_posts` VALUES (37,1,'2022-06-10 05:27:52','2022-06-10 05:27:52','{\n    \"foce-child::story\": {\n        \"value\": \"Loin, tr\\u00e8s loin, au del\\u00e0 des monts Karma, \\u00e0 mille lieues des pays Mizukage et Kagura, demeurent les Nekos Nekos. Ils vivent en retrait, sur les c\\u00f4tes du Funato, un vaste oc\\u00e9an d\\u2019arbres fruitier. Un petit ruisseau coule en leur lieu et les approvisionne en r\\u00e8glalades n\\u00e9cessaires en tout genre; un pays paradisiaque... Sauf pour Kawaneko, le premier chat \\u00e0 avoir d\\u00e9velopp\\u00e9 une intol\\u00e9rance \\u00e0 la fleur d\\u2019oranger, qui malheureusement pour lui est la base de toute productions du pays. Serait-ce une maladie, un virus, le d\\u00e9but d\\u2019une pand\\u00e9mie ou un sort jet\\u00e9 par le sorcier Jaakuna ? Kawaneko et sa bande partent en qu\\u00eate de r\\u00e9ponses pour arr\\u00eater la malveillant Jaakuna et retrouver l\\u2019ordre chez les Nekos Nekos.\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2022-06-10 05:27:52\"\n    }\n}','','','trash','closed','closed','','2f97d5c1-5ed1-4b52-91fb-0b6b9c9a3ae3','','','2022-06-10 05:27:52','2022-06-10 05:27:52','',0,'http://koukaki.local/2f97d5c1-5ed1-4b52-91fb-0b6b9c9a3ae3/',0,'customize_changeset','',0);
